function loadSite() {
    document.getElementById("preloader").style.display = "none";
}
jQuery(function(e) {
    "use strict";
    var o, t, s;
    (o = e(".menu-toggle")),
    (t = e("#offcanvas-menu")),
    (s = e(".close-menu")),
    o.on("click", function() {
            return t.addClass("toggled"), !1;
        }),
        s.on("click", function() {
            return t.removeClass("toggled"), !1;
        }),
        e(window).on("scroll", function() {
            var o;
            (o = e(".topper").height()),
            e(window).scrollTop() > o ?
                e("nav, .menu-toggle").addClass("sticky") :
                e("nav , .menu-toggle").removeClass("sticky");
        }),
        e(".slider-cover").slick({
            dots: !1,
            arrows: !0,
            infinite: !0,
            speed: 1e3,
            slidesToShow: 2,
            slidesToScroll: 1,
            autoplay: !0,
            autoplaySpeed: 2e3,
            adaptiveHeight: !0,
            responsive: [{ breakpoint: 600, settings: { slidesToShow: 1 } }],
        }),
        e(".slider-news").slick({
            dots: !0,
            arrows: !1,
            infinite: !0,
            speed: 1e3,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoplay: !0,
            autoplaySpeed: 2e3,
            adaptiveHeight: !0,
            responsive: [
                { breakpoint: 600, settings: { slidesToShow: 1, dots: !1 } },
            ],
        }),
        e(".main-slider-123").slick({
            dots: !1,
            arrows: !1,
            infinite: !0,
            speed: 1e3,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoplay: !0,
            fade: !0,
            autoplaySpeed: 3e3,
            adaptiveHeight: !0,
            responsive: [
                { breakpoint: 600, settings: { slidesToShow: 1, dots: !1 } },
            ],
        });
});
jQuery(function(e) {
    e(".dropmenu").length &&
        e(".dropmenu").on("click", function() {
            return e(this).find("ul").slideToggle(), !1;
        });
});